mapper.update:
UPDATE users
SET user_password = #{userPassword}, user_clsf = #{userClsf}
WHERE user_email = #{userEmail}

mapper.select:
SELECT user_seq, user_name, user_password, user_email, user_profile_image_url, reg_dt, user_clsf
FROM users
WHERE user_email = #{email}

SELECT user_seq, user_name, user_password, user_email, user_profile_image_url, reg_dt, user_clsf
FROM users
WHERE user_email = #{userEmail} and user_password = #{userPassword}

mapper.update:
UPDATE users
SET token = #{token}
WHERE user_email = #{userEmail}

mapper.select:
SELECT token
FROM users
WHERE user_email = #{userEmail}
