mapper.update:
UPDATE users
SET password = #{password}, code = #{code}
WHERE email = #{email}

mapper.select:
SELECT seq, name, password, email, profile_image_url, reg_dt, code, token
FROM users
WHERE email = #{email}

SELECT seq, name, password, email, profile_image_url, reg_dt, code, token
FROM users
WHERE seq = #{userSeq}

mapper.update:
UPDATE users
SET token = #{token}
WHERE email = #{userEmail}
